import React, { useState, useEffect } from 'react';
import { Link, useLinkClickHandler } from "react-router-dom";
import diamondBlock from './diamondBlock.png';
import diamondSword from './diamondSword.png';
import diamondPickaxe from './diamondPickaxe.png';


const Shop = () => {

  const [items, setItems] = useState([
    {name: "Diamond Block",
    price: 10, 
    img: diamondBlock,
    id: "db",
    quantity: 1}, 
    {name: "Diamond Sword",
    price: 5, 
    img: diamondSword ,
    id: "ds",
    quantity: 1}, 
    {name: "Diamond Pickaxe",
    price: 6, 
    img: diamondPickaxe,
    id: "dp",
    quantity: 1}, 
  ]);

  useEffect(() => {
    console.log(cartArr)
  }, [])

  //activate useEffect so the quantity is rendered at the same time the input value changes and incrementation/decrementation is activated
 const [cartArr, setCartArr] = useState([{name: "Diamond Block",
 price: 10, 
 img: diamondBlock,
 id: "db",
 quantity: 0}, 
