import React, { useState, useEffect } from 'react';
import diamondBlock from './diamondBlock.png'
import diamondSword from './diamondSword.png'
import diamondPickaxe from './diamondPickaxe.png'

const Shop = () => {

  const [items, setItems] = useState([
    {name: "Diamond Block",
    price: "10 MineDollars", 
    img: diamondBlock,
    id: "db",
    quantity: 0,}, 
    {name: "Diamond Sword",
    price: "5 MineDollars", 
    img: diamondSword ,
    id: "ds",
    quantity: 0,}, 
    {name: "Diamond Pickaxe",
    price: "6 MineDollars", 
    img: diamondPickaxe,
    id: "dp",
    quantity: 0,}, 
  ]);





    return (
        <div>
          <div className="barCart">
            <div className="cartSection">Cart</div>
          </div>
          <h1 className="shopTitle">Shop</h1>
           <div className="itemsSection">
           {items.map(item => {
         return (<li className="liItemWrap" key={item.id}>{item.name} <img className="itemImg" src={item.img} alt="imgItem"/> <p> Quantity : {item.quantity}</p> Price : {item.price}</li>)
       })}
           </div>
        </div>
      );
    };

    export default Shop;
