import React, { useState, useEffect } from 'react';
import { Link } from "react-router-dom";

import diamondBlock from './diamondBlock.png';
import diamondSword from './diamondSword.png';
import diamondPickaxe from './diamondPickaxe.png';

const Shop = ({cartArr, setCartArr, cartQuantity, setCartQuantity}) => {

  const [items, setItems] = useState([
    {name: "Diamond Block",
    price: 10, 
    img: diamondBlock,
    id: "db",
    quantity: 0}, 
    {name: "Diamond Sword",
    price: 5, 
    img: diamondSword ,
    id: "ds",
    quantity: 0}, 
    {name: "Diamond Pickaxe",
    price: 6, 
    img: diamondPickaxe,
    id: "dp",
    quantity: 0}, 
  ]);

 const [incr, setIncr] = useState(null)
 const [addToCartNew, setAddToCartState] = useState(null)

 function addToCart(e) {  

    let indexSelectedItem = items.findIndex(item => item.id === e.target.id);
    let itemsArr = items;
    if(itemsArr[indexSelectedItem].quantity === 0) {
      alert("can't buy 0")
    }
    let cartItems = cartArr;

    cartItems[indexSelectedItem].quantity = cartItems[indexSelectedItem].quantity + itemsArr[indexSelectedItem].quantity;
     /* let objItem = {name: itemsArr[indexSelectedItem].name,
    quantity: itemsArr[indexSelectedItem].quantity,
    img: itemsArr[indexSelectedItem].img,
    price: itemsArr[indexSelectedItem].price,
    id: itemsArr[indexSelectedItem].id}  */
    setCartArr(cartItems)
    localStorage.setItem('items', JSON.stringify(cartArr));
    setCartQuantity(cartArr[indexSelectedItem].quantity + cartQuantity)
    localStorage.setItem('cartquant', JSON.stringify(cartQuantity))
      console.log("cart Arr", cartArr)
     setIncr(true)
  /*  setCartQuantity(cartQuantity + itemsArr[indexSelectedItem].quantity)*/
    setAddToCartState(true)


  }


  function incrementQuantity(e) {
    let indexSelectedItem = items.findIndex(item => item.id === e.target.id);
    let itemsArr = items;
    itemsArr[indexSelectedItem].quantity =  itemsArr[indexSelectedItem].quantity + 1;
    setItems(itemsArr)
    setIncr(true)

  }

  function decrementQuantity(e) {

  }

  function handler(e) {
    setIncr(true)
    let indexSelectedItem = items.findIndex(item => item.id === e.target.id);
    let itemsArr = items;
     itemsArr[indexSelectedItem].quantity = parseInt(e.target.value)

     setItems(itemsArr)
    console.log("handler item", itemsArr)
  }

  useEffect(() => {
    if(incr) {
      setItems(items)
      setIncr(false)

    }



  },[ incr, items, cartQuantity])


  useEffect(() => {
    if(addToCart === true){

    }
  },[addToCart])

  useEffect(() => {
    if(incr){
      setCartQuantity(cartQuantity)
    console.log("quantity incr" ,cartQuantity)
    localStorage.setItem('cartquant', JSON.stringify(cartQuantity))
    setIncr(false)
    }
    if(cartQuantity>0) {
      console.log('quantity if 0', cartQuantity)
      localStorage.setItem('cartquant', JSON.stringify(cartQuantity))


    }


  },[cartQuantity, cartArr, incr])

  var retrievedObject = localStorage.getItem('items');

  function renderList() {
    return (items.map(item => {
      return (<li className="liItemWrap" key={item.id}>{item.name} 
      <img className="itemImg" src={item.img} alt="imgItem"/> 
       Unit price : {item.price} 
      <div className="quantitySection">
        <p> Quantity :</p>
         <div className="incrDecrDiv">
           <input value={item.quantity || 0} id={item.id} onChange={(e) => handler(e)} className="quantityInput"/>
           <button id={item.id} onClick={(e) => incrementQuantity(e)} className="incrDecr">+</button>
           <button id={item.id} onClick={(e) => decrementQuantity(e)} className="incrDecr">-</button>
         </div>
      </div>
      <div className="totalPrice">Total price : {parseInt(item.price  || 0 ) * parseInt(item.quantity || 0)}</div>
      <button id={item.id} className="addToCart" onClick={(e) => addToCart(e)}>Add to cart</button></li>)
    }))
  }




    return (
        <div>
          <div className="barCart">
          <Link   to='/shop/cart' state={{ from: JSON.parse(retrievedObject) }}>  <div className="cartSection">Cart ( {localStorage.getItem('cartquant')} ) </div> </Link>
         </div>
          <h1 className="shopTitle">Shop</h1>
           <div className="itemsSection">
           {renderList()}
           </div>
        </div>
      );
    };


    export default Shop;
