import { useLocation } from "react-router-dom"
import React, { useEffect, useState } from "react"

function Cart({cartArr, setCartArr, cartQuantity, setCartQuantity} ) {
  /*  const location = useLocation();
    const { from } = location.state */


    function decrementQuantity(e) {
        let indexSelectedItem = cartArr.findIndex(item => item.id === e.target.id);
        let itemsArr = cartArr;
        itemsArr[indexSelectedItem].quantity =  itemsArr[indexSelectedItem].quantity - 1;
        setCartQuantity(cartQuantity - 1)

    }

    useEffect(() => {
        console.log(cartQuantity)
        console.log('cartarr', cartArr)
        setCartArr(cartArr)
        localStorage.setItem('quant', JSON.stringify(cartQuantity))
        localStorage.setItem('cartArr', JSON.stringify(cartArr))
    },[cartQuantity, cartArr])





    return (<div className="shopingCartContainer">
        <h1>Shoping cart</h1>
        {console.log()}
       {localStorage.getItem('cartArr').map(item => {
           if(item.quantity > 0) { 
           return <div className="wrapItemCart"><img className="cartImgItem" src={item.img} alt="imgItem"></img> 
           <div className="shopCartItemWrap">
               <p>Item : {item.name} </p>
               <p>Quantity : {item.quantity}</p>
               <p>Unit price : {item.price} </p>
               <p>Total price : {parseInt(item.price) * parseInt(item.quantity)}</p>
               </div>
               <div className="totalPrice">Total price : {parseInt(item.price  || 0 ) * parseInt(item.quantity || 0)}</div>
               <button id={item.id} onClick={(e) => decrementQuantity(e)} className="incrDecr">-</button>
               </div>
           }
       })}
        </div>
        )
}



export default Cart
