import React, { useState, useEffect, useRef } from 'react';
import diamondBlock from './diamondBlock.png'
import diamondSword from './diamondSword.png'
import diamondPickaxe from './diamondPickaxe.png'

const Shop = () => {

  const [items, setItems] = useState([
    {name: "Diamond Block",
    price: "10 MineDollars", 
    img: diamondBlock,
    id: "db",
    quantity: 0}, 
    {name: "Diamond Sword",
    price: "5 MineDollars", 
    img: diamondSword ,
    id: "ds",
    quantity: 0}, 
    {name: "Diamond Pickaxe",
    price: "6 MineDollars", 
    img: diamondPickaxe,
    id: "dp",
    quantity: 0}, 
  ]);

 const [currIncr, setCurrIncr] = useState(0);

    useEffect(() => {
      let newItems = items;
      newItems[currIncr].quantity
    },[items])

  function incrementQuantity(e) {
    let indexSelectedItem = items.findIndex(item => item.id === e.target.id);
    let itemsArr = items;
    itemsArr[indexSelectedItem].quantity = itemsArr[indexSelectedItem].quantity + 1;
    setCurrIncr(indexSelectedItem)
    setItems(itemsArr);
    console.log(items)

  }


  function renderList() {
    return (items.map(item => {
      return (<li className="liItemWrap" key={item.id}>{item.name} 
      <img className="itemImg" src={item.img} alt="imgItem"/> 
       Price : {item.price} 
      <p>Quantity : {item.quantity}</p>
      <div className="quantitySection">
        <p> Quantity :</p>
        <div className="incrDecrDiv">
        <input value={item.quantity} id={item.id}  onChange={e => item.quantity = e.target.value} className="quantityInput"/>
        <button id={item.id} onClick={(e) => incrementQuantity(e)} className="incrDecr">+</button>
        <button id={item.id} className="incrDecr">-</button>
        </div>
      </div>
      <button className="addToCart">Add to cart</button></li>)
    }))
  }

    return (
        <div>
          <div className="barCart">
            <div className="cartSection">Cart</div>
          </div>
          <h1 className="shopTitle">Shop</h1>
           <div className="itemsSection">
           {renderList()}
           </div>
        </div>
      );
    };

    export default Shop;
