import { useLocation } from "react-router-dom"
import React, { useEffect, useState } from "react"

function Cart() {
    const location = useLocation();
    const { from } = location.state

    const [incr, setIncr] = useState(null);

    useEffect(() => {
        if(incr) {
            setItemsCart(itemsCart)
            setIncr(false)
        }      
        localStorage.setItem('items', JSON.stringify(from));
    })


    function decrementQuantity(e) {
        let indexSelectedItem = from.findIndex(item => item.id === e.target.id);
        let itemsArr = from;
        itemsArr[indexSelectedItem].quantity =  itemsArr[indexSelectedItem].quantity - 1;
        from = itemsArr
        setIncr(true)
    }

    var retrievedQuant = localStorage.getItem('itemsCart');


    return (<div className="shopingCartContainer">
        <h1>Shoping cart</h1>
        {console.log(from)}
       {itemsCart.map(item => {
           if(item.quantity > 0) { 
           return <div className="wrapItemCart"><img className="cartImgItem" src={item.img} alt="imgItem"></img> 
           <div className="shopCartItemWrap">
               <p>Item : {item.name} </p>
               <p>Quantity : {item.quantity}</p>
               <p>Unit price : {item.price} </p>
               <p>Total price : {parseInt(item.price) * parseInt(item.quantity)}</p>
               </div>
               <button id={item.id} onClick={(e) => decrementQuantity(e)} className="incrDecr">-</button>
               </div>
           }
       })}
        </div>
        )
}



export default Cart
